import React from 'react';
import { motion, useTransform, useViewportScroll } from 'framer-motion';
import { useParallax } from '../../hooks/useAnimation';

const ParallaxElement = ({ 
  children, 
  speed = 0.5, 
  className = '',
  direction = 'up',
  ...props 
}) => {
  const { scrollY } = useViewportScroll();

  const getTransform = () => {
    switch (direction) {
      case 'up':
        return useTransform(scrollY, [0, 1000], [0, -speed * 100]);
      case 'down':
        return useTransform(scrollY, [0, 1000], [0, speed * 100]);
      case 'left':
        return useTransform(scrollY, [0, 1000], [0, -speed * 100]);
      case 'right':
        return useTransform(scrollY, [0, 1000], [0, speed * 100]);
      default:
        return useTransform(scrollY, [0, 1000], [0, -speed * 100]);
    }
  };

  const transform = getTransform();

  const getStyle = () => {
    if (direction === 'left' || direction === 'right') {
      return { x: transform };
    }
    return { y: transform };
  };

  return (
    <motion.div
      className={className}
      style={getStyle()}
      {...props}
    >
      {children}
    </motion.div>
  );
};

export default ParallaxElement;
